INSERT INTO `wn_posts` VALUES (156, 1, '2015-08-10 12:02:19', '2015-08-10 09:02:19', 'يستضيف برنامج 90 دقيقة، مع الاعلامي محمد مصطفي شردى على قناة المحور الفضائية الليلة، المهندس أسامه كمال وزير البترول الأسبق، واللواء شيرين حسن رئيس قطاع النقل البحري الأسبق ورئيس هيئة موانئ بورسعيد السابق.\r\nويجيب الضيفان على أسئلة ماذا بعد افتتاح قناة السويس الجديدة؟ وهل تعوق أزمة الطاقة التنمية في محور القناة ؟ وكيف تتم عملية توسعة قناة شرق بورسعيد ؟ وهل تعمل علي زيادة حركة الملاحة العالمية ؟ وهل نحتاج الي توسعة وتكريك تفريعات أخري؟.\r\n\r\n&nbsp;', '"كمال" و"حسن" ضيفا 90 دقيقة للحديث عن المستقبل الاقتصادي بعد افتتاح قناة السويس', 'يستضيف برنامج 90 دقيقة، مع الاعلامي محمد مصطفي شردى على قناة المحور الفضائية الليلة، المهندس أسامه كمال وزير البترول الأسبق، واللواء شيرين حسن رئيس قطاع النقل البحري الأسبق ورئيس هيئة موانئ بورسعيد السابق.\r\nويجيب الضيفان على أسئلة ماذا بعد افتتاح قناة السويس الجديدة؟ وهل تعوق أزمة الطاقة التنمية في محور القناة ؟ وكيف تتم عملية توسعة قناة شرق بورسعيد ؟ وهل تعمل علي زيادة حركة الملاحة العالمية ؟ وهل نحتاج الي توسعة وتكريك تفريعات أخري؟.', 'publish', 'open', 'open', '', '%d9%83%d9%85%d8%a7%d9%84-%d9%88%d8%ad%d8%b3%d9%86-%d8%b6%d9%8a%d9%81%d8%a7-90-%d8%af%d9%82%d9%8a%d9%82%d8%a9-%d9%84%d9%84%d8%ad%d8%af%d9%8a%d8%ab-%d8%b9%d9%86-%d8%a7%d9%84%d9%85%d8%b3%d8%aa', '', '', '2015-09-20 16:53:01', '2015-09-20 13:53:01', '', 0, 'http://localhost/waseetnews/?p=156', 0, 'post', '', 0);
