INSERT INTO `wn_posts` VALUES (82, 1, '2015-08-08 13:48:14', '2015-08-08 10:48:14', 'فاز الزمالك بالدورى،عنوان إشتقنا اليه بعد مرور سنوات عجاف تجرعنا فيها كل أنواع الشد العصبى ورغم هذا ظل حب هذا الكيان أبدى لم يهتز ولو للحظة لليقين التام أن حب الزمالك لا يفهمه سوى عاشقوه،موسم طويل ذقنا فيه كل أنواع الضغط لما لا فالمنافس جن جنونه وهو يرسم لنفسه كل المبررات الممكنه والغير للتأثير على المتصدر فهو لم يتذوقه طوال الـ 38 اسبوع ولكن الزمالك ارتدى ثوب الابطال وتكاتف لاعبوه نحو تحقيق الحلم وتحدوا كل ما قيل من حكام وبريزنتيشان وغيرها الى ان تبقى شئ واحد فقط عشان تكمل القصة وهى " الإخوان إدوا الزمالك الدورى"عشان تكمل!!\r\nاسمحوا لى أن اختصر لكم الموسم ومادار بذهنى فى السطور القادمه،لعل وعسى استطيع ان انقل ما دار بداخل الكثير منا. الزمالك بطل الكأس مع ميدو..طب هيكمل ولا لأ؟كمل..ماتشات افريقيا..خروج..هيتم اقالته؟لا مش هيتم..حسام حسن؟تانى..هيبقى البديل..ليه؟..عايزين نقله تقوم تجيب حسام؟!لا هو حسام..اقالة ميدو..عين حسام..العب سوبر..اخسر سوبر..نحس بعيد عنك بعد ضربات جزاء درامية..خناقة مرتضى وحسام..اتصالحوا..مرتضى يعلنها حسام هيكمل مهما كان..بداية الدورى وفوز كبير عالجيش..احتراف عبد الشافى..يمشى مايمشيش..فلوس هبل..مرتضى قبل فحسام', 'الحكومة تضخ مليار دولار للسيطره على سعر الدولار قبل حلول رمضان', 'لا مش هيتم..حسام حسن؟تانى..هيبقى البديل..ليه؟..عايزين نقله تقوم تجيب حسام؟!لا هو حسام..اقالة ميدو..عين حسام..العب سوبر..اخسر سوبر..نحس بعيد عنك بعد ضربات جزاء درامية..خناقة مرتضى وحسام..اتصالحوا..مرتضى يعلنها حسام هيكمل مهما كان..بداية الدورى ', 'publish', 'open', 'open', '', '%d8%a7%d9%84%d8%ad%d9%83%d9%88%d9%85%d8%a9-%d8%aa%d8%b6%d8%ae-%d9%85%d9%84%d9%8a%d8%a7%d8%b1-%d8%af%d9%88%d9%84%d8%a7%d8%b1-%d9%84%d9%84%d8%b3%d9%8a%d8%b7%d8%b1%d9%87-%d8%b9%d9%84%d9%89-%d8%b3%d8%b9-3', '', '', '2015-08-10 12:15:22', '2015-08-10 09:15:22', '', 0, 'http://localhost/waseetnews/?p=82', 0, 'post', '', 0);
