INSERT INTO `wn_posts` VALUES (148, 1, '2015-08-09 16:17:34', '2015-08-09 13:17:34', 'تكثف الاجهزة الامنية بالجيزة جهودها لإلقاء القبض على عامل اطلق الرصاص على زوجته وحماته وقتلهما بمنطقة بولاق الدكرور بعد هروبه عقب ارتكاب الجريمة.\r\n\r\nووجه اللواء مجدي عبد العال مدير الادارة العامة للمباحث بخروج عدة ماموريات من ضباط مباحث بولاق الدكرور بحثا عن المتهم حيث خرجت حملات تراسها العقيد عمرو حافظ نائب مامور بولاق الدكرور والمقدم هاني الحسيني رئيس المباحث واستمعت لشهود الواقعة وشقيقة الزوجة المصابة برش خرطوش وحصلوا علي مواصفات تفصيليه للزوج المتهم بالجريمة.\r\n\r\nبدأت الواقعة عقب وقوع خلافات بين ربة منزل وزوجها فتركت منزل الزوجية منذ قرابة 20 يوما وتوجهت للاقامة بمنزل عمها حتي عادت الة منزل والدها القريب من منزل زوجها بمنطقة بولاق الدكرور.\r\n\r\nوفور علم المتهم ويعمل "عامل"، بعودة للزوجة الى منزل اسرتها توجه اليهم فرأته شقيقة زوجته التي اعتقدت انه حضر للتصالح مع شقيقتها ففتحت له باب المنزل الا انه فاجئها بإحكام قبضته على رقبتها ودفعها عالى الارض وقام باخراج بندقية خرطوش من حقيبة كان يحملها فشاهد حماته تقف امامه تؤدي الصلاة فاطلق عليه عدة طلقات اردتها قتيلة.\r\n\r\nفهرعت زوجته من غرفتها على صوت دوي الرصاص وما ان شاهدها حتى اطلق عليها عدة طلقات ايضا اودت بحياتها واستدار باحثا عن شقيقة زوجته التي فتحت له باب الشقة ليجدها خرجت مسرعة تصرخ خارج باب الشقة فاطلق عليها الرصاص ايضا الا انها اصيبت برش خرطوش ولم تلق مصرعها.', 'جهود أمنية لإلقاء القبض على عامل قتل زوجته وحماته ببولاق الدكرور', 'ووجه اللواء مجدي عبد العال مدير الادارة العامة للمباحث بخروج عدة ماموريات من ضباط مباحث بولاق الدكرور بحثا عن المتهم حيث خرجت حملات تراسها العقيد عمرو حافظ نائب مامور بولاق الدكرور والمقدم هاني الحسيني رئيس المباحث واستمعت لشهود الواقعة وشقيقة الزوجة المصابة برش خرطوش وحصلوا علي مواصفات تفصيليه للزوج المتهم بالجريمة.', 'publish', 'open', 'open', '', '%d8%ac%d9%87%d9%88%d8%af-%d8%a3%d9%85%d9%86%d9%8a%d8%a9-%d9%84%d8%a5%d9%84%d9%82%d8%a7%d8%a1-%d8%a7%d9%84%d9%82%d8%a8%d8%b6-%d8%b9%d9%84%d9%89-%d8%b9%d8%a7%d9%85%d9%84-%d9%82%d8%aa%d9%84-%d8%b2%d9%88', '', '', '2015-08-09 16:17:34', '2015-08-09 13:17:34', '', 0, 'http://localhost/waseetnews/?p=148', 0, 'post', '', 0);
