INSERT INTO `wn_posts` VALUES (217, 1, '2015-08-10 17:47:26', '2015-08-10 14:47:26', 'اكد ماهر فرغلي الكاتب والباحث في شئون الحركات الاسلامية ، أن سمة الجماعة الاسلامية خلال الفترة المقبلة بعد وفاة عصام دربالة رئيس مجلس شورى الجماعة ستكون الخمول والكمون ، لافتا إلى ان قيادة الجماعة القادمة ستكون أضعف من ان تتجه نحو العنف ، وستبحث عن مخرج من العنف وستؤكد السلمية مع الدولة.&lt;br/ &gt;\r\nواضاف فرغلي في تصريحات لـ "صدى البلد" ان "دربالة " كان ممسكا بكل خيوط "اللعبة " في يده، داخل وخارج مصر وأيضا داخل وخارج السجون.\r\nوتابع : " خلال الفترة المقبلة سيتم إعادة انتخاب مجلس شورى جديد للجماعة ، والقيادات الموجودة حاليا خارج السجون كلها شرعية وليست مخالفة للقانون لذا فاتجاه الجماعة للعنف بعد وفاة دربالة مستبعد".', 'فرغلي: اتجاه الجماعة الاسلامية للعنف بعد وفاة دربالة "مستبعد"', '', 'inherit', 'open', 'open', '', '167-revision-v1', '', '', '2015-08-10 17:47:26', '2015-08-10 14:47:26', '', 167, 'http://localhost/waseetnews/167-revision-v1/', 0, 'revision', '', 0);
