INSERT INTO `wn_posts` VALUES (265, 1, '2015-08-11 16:17:38', '2015-08-11 13:17:38', 'زودت شركة فورد الأمريكية شاحنتها F-150 البيك أب ذات ناقل الحركة الأوتوماتيك سداسي السرعات بوضعية سبورت.\r\n\r\nولأول مرة، فإن F-150 تعرض بوضعية سبورت حسب الطلب ، وهي الوضعية التي تستهدف تحسين تجربة قيادة الشاحنة.\r\n\r\nوتعرض هذه الوضعية بصورة قياسية مع السيارة بناقل الحركة الأوتوماتيك من 6 سرعات ، فيما تعمل وضعية سبورت بنظام الدفع الثنائي والرباعي بالناقل الأوتوماتيك أو بنظام الدفع الرباعي في أعلي إصداراته.\r\n\r\nوتغير وضعية سبورت تردد ناقل الحركة وبما يحفظ المحرك في صورته المثالية ويوفر كذلك متعة خاصة في القيادة بسبب تقليل زمن النقلات خاصة علي الطرق الجبلية.\r\n\r\nكما أن وضعية سبورت تقلل من الضغط علي دواسة الوقود للوصول للعزم المطلوب لزيادة تسارع السيارة.', 'فورد تزود F- 150 الأتوماتيك بوضعية سبورت', 'وتغير وضعية سبورت تردد ناقل الحركة وبما يحفظ المحرك في صورته المثالية ويوفر كذلك متعة خاصة في القيادة بسبب تقليل زمن النقلات خاصة علي الطرق الجبلية.\r\nكما أن وضعية سبورت تقلل من الضغط علي دواسة الوقود للوصول للعزم المطلوب لزيادة تسارع السيارة. ', 'publish', 'open', 'open', '', '%d9%81%d9%88%d8%b1%d8%af-%d8%aa%d8%b2%d9%88%d8%af-f-150-%d8%a7%d9%84%d8%a3%d8%aa%d9%88%d9%85%d8%a7%d8%aa%d9%8a%d9%83-%d8%a8%d9%88%d8%b6%d8%b9%d9%8a%d8%a9-%d8%b3%d8%a8%d9%88%d8%b1%d8%aa', '', '', '2015-08-11 16:17:38', '2015-08-11 13:17:38', '', 0, 'http://localhost/waseetnews/?p=265', 0, 'post', '', 0);
