INSERT INTO `wn_posts` VALUES (273, 1, '2015-08-11 16:51:16', '2015-08-11 13:51:16', 'أعلن ناديا بورصا سبور التركي وثون السويسري عن رغبتهما في ضم المدافع المصري الشاب رامي ربيعة لاعب فريق سبورتنج لشبونة البرتغالي.\r\n\r\nوكان ربيعة قد خرج من حسابات جورجي خيسوس المدير الفني لسبورتنج لشبونة، وخرج من قائمة الفريق الأول، لينضم لتدريبات الفريق الثاني "سبورتنج B" الذي يلعب في دوري الدرجة الثانية.\r\n\r\nورفض ربيعة استكمال مشواره مع الفريق الثاني، وقرر الرحيل عن صفوف الفريق البرتغالي.\r\n\r\nودخل ناديا بورصا سبور وثون في مفاوضات مع سبورتنج لضم اللاعب المصري.', 'بورصا سبور التركي وثون السويسري يتنافسان على ضم ربيعة', 'وكان ربيعة قد خرج من حسابات جورجي خيسوس المدير الفني لسبورتنج لشبونة، وخرج من قائمة الفريق الأول، لينضم لتدريبات الفريق الثاني "سبورتنج B" الذي يلعب في دوري الدرجة الثانية.', 'publish', 'open', 'open', '', '%d8%a8%d9%88%d8%b1%d8%b5%d8%a7-%d8%b3%d8%a8%d9%88%d8%b1-%d8%a7%d9%84%d8%aa%d8%b1%d9%83%d9%8a-%d9%88%d8%ab%d9%88%d9%86-%d8%a7%d9%84%d8%b3%d9%88%d9%8a%d8%b3%d8%b1%d9%8a-%d9%8a%d8%aa%d9%86%d8%a7%d9%81', '', '', '2015-08-11 16:51:16', '2015-08-11 13:51:16', '', 0, 'http://localhost/waseetnews/?p=273', 0, 'post', '', 0);
