INSERT INTO `wn_posts` VALUES (281, 1, '2015-08-11 16:52:27', '2015-08-11 13:52:27', 'اقترب نادي روما الإيطالى من التعاقد مع الثنائى جيرسون لاعب وسط فلومينينزى البرازيلى ولوكاس دين ظهير فريق باريس سان جيرمان الفرنسي.\r\n\r\nوقالت تقارير إيطالية إن الثنائى سيخضعان للكشف الطبية في نادي روما خلال ساعات قبل انضمامهما رسميا للفريق.\r\n\r\nوكان روما قد ضم الثنائي محمد صلاح جناح فريق تشيلسي الإنجليزي، والمهاجم البوسني إدين دزيكو مهاجم مانشيستر سيتي الإنجليزي.', 'بعد صلاح ودزيكو.. روما يقترب من حسم صفقتين جديدتين', 'اقترب نادي روما الإيطالى من التعاقد مع الثنائى جيرسون لاعب وسط فلومينينزى البرازيلى ولوكاس دين ظهير فريق باريس سان جيرمان الفرنسي.', 'publish', 'open', 'open', '', '%d8%a8%d8%b9%d8%af-%d8%b5%d9%84%d8%a7%d8%ad-%d9%88%d8%af%d8%b2%d9%8a%d9%83%d9%88-%d8%b1%d9%88%d9%85%d8%a7-%d9%8a%d9%82%d8%aa%d8%b1%d8%a8-%d9%85%d9%86-%d8%ad%d8%b3%d9%85-%d8%b5%d9%81%d9%82%d8%aa', '', '', '2015-08-11 16:52:36', '2015-08-11 13:52:36', '', 0, 'http://localhost/waseetnews/?p=281', 0, 'post', '', 0);
