INSERT INTO `wn_posts` VALUES (300, 1, '2015-08-11 17:19:21', '2015-08-11 14:19:21', 'نفى المهندس محمد إبراهيم الأخرس وكيل وزارة الكهرباء بمحافظة بورسعيد ما تداولة بعض المواقع الإليكترونية عن إنقطاع التيار الكهربائى عن بنك الدم أمس الأثنين لمدة ساعتان وأكد أن بنك الدم قام بتركيب مولد خاص تم الاشراف علية من قبل الشركة مجاناً ليقوم بالعمل عند حدوث أعطال مفاجأة أو أعمال صيانة لإستمرار التغذية الكهربائية ببنك الدم .\r\n\r\nوأشار وكيل الوزارة انة أصدر تعليماته بإعلان حالة الطوارئ بالقطاع لمواجهة ارتفاع درجات الحرارة التى من المحتمل أن تصل إلى 45 درجة مئوية وإنشاء غرفة طوارئ لتلقى البلاغات عن انقطاع التيار الكهربائى بجميع أنحاء بورسعيد ومدينة بورفؤاد.', 'كهرباء بورسعيد تنفى إنقطاع التيارعن بنك الدم', '', 'publish', 'open', 'open', '', '%d9%83%d9%87%d8%b1%d8%a8%d8%a7%d8%a1-%d8%a8%d9%88%d8%b1%d8%b3%d8%b9%d9%8a%d8%af-%d8%aa%d9%86%d9%81%d9%89-%d8%a5%d9%86%d9%82%d8%b7%d8%a7%d8%b9-%d8%a7%d9%84%d8%aa%d9%8a%d8%a7%d8%b1%d8%b9%d9%86-%d8%a8', '', '', '2015-08-11 17:19:21', '2015-08-11 14:19:21', '', 0, 'http://localhost/waseetnews/?p=300', 0, 'post', '', 0);
