INSERT INTO `wn_posts` VALUES (321, 1, '2015-08-11 17:41:01', '2015-08-11 14:41:01', 'مجلة «ماري كلير» العالمية تقريرا بالفيديو و الصور حول موضة اعادة تشكيل الأظافر من جديد و التي بدأت تكتسح بيوت التجميل العالمية و انتشرت بقوة في الغرب من خلال موقع انستجرام للتواصل الاجتماعي.\r\n\r\nو يقول التقرير إنه بالأمس كانت المرأة تتخلص من حواجبها ثم تعيد تشكيل حواجب جديدة تماما.ثم الأسنان ثم أعضاء الجسم و نحته و تغيير شكله و إعادة تشكيله، واليوم جاء الدور على الأظافر التي يمكن للمرأة اليوم اختيار إن كانت طويلة و مثلثة أم مرتفعة و منتتفخة أم مدببة و ملتوية .', 'بالفيديو والصور .. «إعادة تشكيل الأظافر» موضة تكتسح بيوت الأزياء العالمية', 'مجلة «ماري كلير» العالمية تقريرا بالفيديو و الصور حول موضة اعادة تشكيل الأظافر من جديد و التي بدأت تكتسح بيوت التجميل العالمية و انتشرت بقوة في الغرب من خلال موقع انستجرام للتواصل الاجتماعي.', 'publish', 'open', 'open', '', '%d8%a8%d8%a7%d9%84%d9%81%d9%8a%d8%af%d9%8a%d9%88-%d9%88%d8%a7%d9%84%d8%b5%d9%88%d8%b1-%d8%a5%d8%b9%d8%a7%d8%af%d8%a9-%d8%aa%d8%b4%d9%83%d9%8a%d9%84-%d8%a7%d9%84%d8%a3%d8%b8%d8%a7%d9%81%d8%b1', '', '', '2015-08-11 17:41:01', '2015-08-11 14:41:01', '', 0, 'http://localhost/waseetnews/?p=321', 0, 'post', '', 0);
